CREATE TABLE Books
(
    ID INT PRIMARY KEY AUTO_INCREMENT,
    AuthorID INT,
    ISBN VARCHAR(13) UNIQUE,
    Title VARCHAR(255),
    CoverURL VARCHAR(255),
    ReleaseYear VARCHAR(255),
    Edition VARCHAR(255),
    Publisher VARCHAR(255),
    PublisherOrigin VARCHAR(255),
    Language VARCHAR(255),
    Pages INT,
    Synopsis TEXT,
    Rating INT DEFAULT 0,
    HardCopy BOOLEAN DEFAULT 0,
    EBook BOOLEAN DEFAULT 0,
    AudioBook BOOLEAN DEFAULT 0,
	Views INT DEFAULT 0,
    Borrowed BOOLEAN DEFAULT 0,
    Bookshelf VARCHAR(255),
    CreatedAt DATETIME DEFAULT (NOW()),
    UpdatedAt DATETIME DEFAULT (NOW())
);

ALTER TABLE Books ADD CONSTRAINT BooksFKAuthorID FOREIGN KEY (AuthorID) REFERENCES Authors (ID);

